import './App.css';
import React from 'react';
import { Formik, useField, Form } from 'formik';
import * as Yup from 'yup';
import principalImg from './main-assets/cellphone.png';
import voxsweetLogo from './main-assets/Logo.png';

const TextInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);

  return (
    <>
      <input className="text-input" {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ) : null}
    </>
  )
}

function App() {
  return (
    <Formik
      initialValues={{
        email: '',
        password: '',
      }}

      validationSchema={Yup.object({
        email: Yup.string()
          .max(100, 'Você excedeu a quantidade máxima de caracteres para o email.')
          .required('O e-mail é obrigatório.'),
        senha: Yup.string()
          .max(50, 'A senha deve ter no máximo 50 caracteres.')
          .min(6, 'A senha deve ter no mínimo 6 caracteres.')
          .required('A senha é obrigatório.')
      })}

      onSubmit={(values, { setSubmitting, resetForm }) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2));
          resetForm();
          setSubmitting(false);
        }, 3000)
      }}

    >
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm ">
            <img className="principal-img img-fluid" src={principalImg} alt="" />
          </div>

          <div className="col-sm principal-content">
            <img className="logo-img img-fluid" src={voxsweetLogo} alt="" />
            <h1 className="font-vosweet text-items">Vosweet</h1>
            <p className="main-text text-items">
              Uma mensagem em um click.
            </p>
          </div>
        </div>
      </div>

      {props => (
        <div className="form-box">
          <Form className="form-control-style">

            <h1 className="login-text"> Faça o login na sua conta! </h1>
            <TextInput
              name="email"
              type="email"
              placeholder="E-mail"
              className="input-box" />

            <TextInput
              name="password"
              type="password"
              placeholder="Senha"
              className="input-box" />

            <button className="login-button" type="submit"> Entrar</button>

            <div className="linha-button"></div>

            <p className="text-cadastro">Não tem uma conta?<a href="#cadastro" className="cadastro-link">Cadastre-se</a></p>
          </Form>
        </div>
      )
      };
    </Formik >
  )
}






















