import React from 'react'
import { Formik, Form } from 'formik';
import * as Yup from 'yup';
// import { TextField } from "./TextField"

export const Login = () => {
    const validate = Yup.object({
        email: Yup.string()
            .max(100, 'Você excedeu a quantidade máxima de caracteres para o email.')
            .required('O e-mail é obrigatório.'),
        senha: Yup.string()
            .max(50, 'A senha deve ter no máximo 50 caracteres.')
            .min(6, 'A senha deve ter no mínimo 6 caracteres.')
            .required('A senha é obrigatório.')
    });

    return (
        <Formik
            initialValues={{
                email: "",
                password: ""
            }}
            onSubmit={(values, { setSubmitting }) => {
                setTimeout(() => {
                    console.log("Logging in", values)
                }, 500);
            }}
            validationSchema={validate}
        >

            {props => {
                const {
                    values,
                    touched,
                    erros,
                    isSubmitting,
                    handleChange,
                    handleBlur,
                    handleSubmit,
                } = props;

               
                    <Form className="form-control-style" autocomplete="off" onSubmit={handleSubmit}>

                        <h1 className="login-text"> Faça o login na sua conta! </h1>
                        <input
                            name="email"
                            value={values.email}
                            type="email"
                            placeholder="E-mail"
                            className="input-box" />

                        <input
                            name="password"
                            value={values.password}
                            type="password"
                            placeholder="Senha"
                            className="input-box" />

                        <button className="login-button" type="submit">Entrar</button>

                        <div className="linha-button"></div>

                        <p className="text-cadastro">Não tem uma conta? <a href="#cadastro" className="cadastro-link">Cadastre-se</a></p>

                    </Form>
            
            }}

        </Formik>
    )
}
